#ifdef API3D_OPENGL20
void C3DAPIBASE::setPO(CMatrix *m,float w,float h)
{

	m->a[0][0] = 2.0f/w;
	m->a[1][0] = 0.0f;
	m->a[2][0] = 0.0f;
	m->a[3][0] = 0.0f;

	m->a[0][1] = 0.0f;

    if (SelectedRenderTarget>=0) m->a[1][1] = 2.0f/h;
	else m->a[1][1] = -2.0f/h;

	m->a[2][1] = 0.0f;
	m->a[3][1] = 0.0f;

	m->a[0][2] = 0.0f;
	m->a[1][2] = 0.0f;
	m->a[2][2] = 2.0f;
	m->a[3][2] = -1.0f;

	m->a[0][3] = 0.0f;
	m->a[1][3] = 0.0f;
	m->a[2][3] = 0.0f;
	m->a[3][3] = 1.0f;
}
#endif

/* ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////

  function:

		Check OpenGL extension strings	

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////// */

bool CheckExtension(char *extName, const char *extString)
{
    char *p = (char *)extString;
    char *end;
    int extNameLen;

    extNameLen = strlen(extName);
    end = p + strlen(p);

    while (p < end) {
	int n = strcspn(p, " ");
	if ((extNameLen == n) && (strncmp(extName, p, n) == 0)) {
	    return true;
	}
	p += (n + 1);
    }
    return false;
}

