"		oPos.w = 1.0;\n"
"		oPos.xy += d.xy;\n"
"	[DIFFUSE]\n"
"		oDiffuse = iDiffuse;\n"
"	[MAPPING0]\n"
"		oTexture0.xy = iTexture0.xy;\n"
"	[SHADER]\n"
"		col = sample(0,Tex0);\n"
"		if (col.a<0.5) discard;\n"
"		Out = Diffuse;\n"
"    [RENDER_STATE]\n"
"		Blend False\n"
"		Texture[0] Tex\n"
"		TextureCoordinate[0] TexCoo0\n"

"[END]\n"
"\n";

