		cmd->ClearRenderTargetView(rtvSurface, clearColor, 0, nullptr);
		cmd->ClearDepthStencilView(dsvDepthStencil,D3D12_CLEAR_FLAG_DEPTH|D3D12_CLEAR_FLAG_STENCIL,1.0f,0,0,nullptr);
