                case INVERT:
                    StencilREF=0x0;
                    depthStencilDesc.StencilEnable = true;
                    depthStencilDesc.FrontFace.StencilFailOp = D3D12_STENCIL_OP_INCR;
                    depthStencilDesc.FrontFace.StencilDepthFailOp = D3D12_STENCIL_OP_INCR;
