void glSetRenderState(vpRenderState * RS,C3DAPIBASE *render)
{
	unsigned int s,d;

	if (RS)
	{
		if ((RS->ZBufferWrite!=_UNDEFINED)||(RS->ZBuffer!=_UNDEFINED))
		{
			render->SetParams(API3D_ZBUFFER,ON);
			if (RS->ZBufferWrite==_FALSE) render->SetParams(API3D_ZBUFFER,WRITEOFF);
			if (RS->ZBuffer==_FALSE) render->SetParams(API3D_ZBUFFER,OFF);
		}

		if (RS->ShadeMode==_SMOOTH) render->SetParams(API3D_SHADING,ON);
		if (RS->ShadeMode==_FLAT) render->SetParams(API3D_SHADING,OFF);

		int val=0;

		if (RS->zbias!=_UNDEFINED)
		{
			switch (RS->zbias)
			{
			case _OFFSET_0:
				val=0;
				break;
			case _OFFSET_1:
				val=1;
				break;
			case _OFFSET_2:
				val=2;
				break;
			case _OFFSET_3:
				val=3;
				break;
			case _OFFSET_4:
				val=4;
				break;
			case _OFFSET_5:
				val=5;
				break;
			case _OFFSET_6:
				val=6;
				break;
			case _OFFSET_7:
				val=7;
				break;

			};
			render->SetParams(API3D_ZBIAS,val);
		}


#define glDEF 2

		int n;
		unsigned int tag;
		unsigned int rgb_mul[glDEF],rgb_s0[glDEF],rgb_s1[glDEF],rgb_op[glDEF];
		unsigned int a_mul[glDEF],a_s0[glDEF],a_s1[glDEF],a_op[glDEF];

		if (render->MultiTexture)
		{

			for (n=0;n<2;n++)
			{
				switch (RS->AArg1[n])
				{
				case _TEXTURE:

					a_s0[n]=GL_TEXTURE;
					break;
				case _DIFFUSE:
					a_s0[n]=GL_PRIMARY_COLOR;
					break;
				case _CURRENT:
					a_s0[n]=GL_PREVIOUS;
					break;
				case _SPECULAR:
					a_s0[n]=GL_PRIMARY_COLOR;
					break;
				};

				switch (RS->AArg2[n])
				{
				case _TEXTURE:
					a_s1[n]=GL_TEXTURE;
					break;
				case _DIFFUSE:
					a_s1[n]=GL_PRIMARY_COLOR;
					break;
				case _CURRENT:
					a_s1[n]=GL_PREVIOUS;
					break;
				case _SPECULAR:
					a_s1[n]=GL_PRIMARY_COLOR;
					break;
				};

				switch (RS->CArg1[n])
				{
				case _TEXTURE:
					rgb_s0[n]=GL_TEXTURE;
					break;
				case _DIFFUSE:
					rgb_s0[n]=GL_PRIMARY_COLOR;
					break;
				case _CURRENT:
					rgb_s0[n]=GL_PREVIOUS;
					break;
				case _SPECULAR:
					rgb_s0[n]=GL_PRIMARY_COLOR;
					break;
				};

				switch (RS->CArg2[n])
				{
				case _TEXTURE:
					rgb_s1[n]=GL_TEXTURE;
					break;
				case _DIFFUSE:
					rgb_s1[n]=GL_PRIMARY_COLOR;
					break;
				case _CURRENT:
					rgb_s1[n]=GL_PREVIOUS;
					break;
				case _SPECULAR:
					rgb_s1[n]=GL_PRIMARY_COLOR;
					break;
				};


				a_op[n]=GL_REPLACE;
				a_mul[n]=1;
				switch (RS->AOp[n])
				{
				case _MODULATE:
					a_op[n]=GL_MODULATE;
					break;
				case _MODULATE2X:
					a_op[n]=GL_MODULATE;
					a_mul[n]=2;
					break;
				case _ADD:
					a_op[n]=GL_ADD;
					break;
				case _ADDSIGNED:
					a_op[n]=GL_ADD_SIGNED;
					break;
				case _SUBTRACT:
					a_op[n]=GL_SUBTRACT;
					break;
				case _DISABLE:
					break;
				case _DOT3:
					a_op[n]=GL_DOT3_RGB;
					break;
				};



				tag=1;

				rgb_op[n]=GL_REPLACE;
				rgb_mul[n]=1;
				switch (RS->COp[n])
				{
				case _MODULATE:
					rgb_op[n]=GL_MODULATE;
					break;
				case _ADDSIGNED:
					rgb_op[n]=GL_ADD_SIGNED;
					break;
				case _MODULATE2X:
					rgb_op[n]=GL_MODULATE;
					rgb_mul[n]=2;
					break;
				case _ADD:
					rgb_op[n]=GL_ADD;
					break;
				case _SUBTRACT:
					rgb_op[n]=GL_SUBTRACT;
					break;
				case _DISABLE:
					tag=0;
					break;
				case _DOT3:
					rgb_op[n]=GL_DOT3_RGB;
					break;
				};

				if (tag)
				{
					glActiveTexture(GL_TEXTURE0+n);
					glEnable(GL_TEXTURE_2D);
					glTexEnvi(GL_TEXTURE_ENV,GL_TEXTURE_ENV_MODE,GL_COMBINE);
/*
					glTexEnvi(GL_TEXTURE_ENV,GL_SOURCE0_RGB,rgb_s0[n]);
					glTexEnvi(GL_TEXTURE_ENV,GL_SOURCE1_RGB,rgb_s1[n]);
					glTexEnvi(GL_TEXTURE_ENV,GL_OPERAND0_RGB,GL_SRC_COLOR);
					glTexEnvi(GL_TEXTURE_ENV,GL_OPERAND1_RGB,GL_SRC_COLOR);

					glTexEnvi(GL_TEXTURE_ENV,GL_SOURCE0_ALPHA,a_s0[n]);
					glTexEnvi(GL_TEXTURE_ENV,GL_SOURCE1_ALPHA,a_s1[n]);
					glTexEnvi(GL_TEXTURE_ENV,GL_OPERAND0_ALPHA,GL_SRC_ALPHA);
					glTexEnvi(GL_TEXTURE_ENV,GL_OPERAND1_ALPHA,GL_SRC_ALPHA);
/**/
					glTexEnvi(GL_TEXTURE_ENV,GL_COMBINE_RGB,rgb_op[n]);
					glTexEnvi(GL_TEXTURE_ENV,GL_COMBINE_ALPHA,a_op[n]);

					glTexEnvi(GL_TEXTURE_ENV,GL_RGB_SCALE,rgb_mul[n]);
//					glTexEnvi(GL_TEXTURE_ENV,GL_ALPHA_SCALE,a_mul[n]);
				}
				else
				{
					glActiveTexture(GL_TEXTURE0+n);
					glDisable(GL_TEXTURE_2D);
				}
			}
		}
		else
		{
			switch (RS->COp[0])
			{
			case _MODULATE:
				rgb_op[0]=GL_MODULATE;
				break;
			case _ADDSIGNED:
				rgb_op[0]=GL_ADD;
				break;
			case _MODULATE2X:
				rgb_op[0]=GL_MODULATE;
				break;
			case _ADD:
				rgb_op[0]=GL_ADD;
				break;
			case _SUBTRACT:
				rgb_op[0]=GL_ADD;
				break;
			case _DISABLE:
				tag=0;
				break;
			case _DOT3:
				rgb_op[0]=GL_MODULATE;

				break;
			};

			if (tag)
			{
				glEnable(GL_TEXTURE_2D);
				glTexEnvi(GL_TEXTURE_ENV,GL_TEXTURE_ENV_MODE,rgb_op[0]);
			}
			else glDisable(GL_TEXTURE_2D);
		}

		if (RS->Dither!=_UNDEFINED)
		{
			switch(RS->Dither)
			{
			case _TRUE:
				render->SetParams(API3D_DITHERING,ON);
				break;
			case _FALSE:
				render->SetParams(API3D_DITHERING,OFF);
				break;
			};
		}

		if (RS->SrcBlend!=_UNDEFINED)
		{
			switch (RS->SrcBlend)
			{
				case _ZERO:
					s=GL_ZERO;
					break;
				case _ONE:
					s=GL_ONE;
					break;
				case _SRCALPHA:
					s=GL_SRC_ALPHA;
					break;
				case _INVSRCALPHA:
					s=GL_ONE_MINUS_SRC_ALPHA;
					break;
				case _SRCCOLOR:
					s=GL_SRC_COLOR;
					break;
				case _INVSRCCOLOR:
					s=GL_ONE_MINUS_SRC_COLOR;
					break;
				case _DSTALPHA:
					s=GL_DST_COLOR;
					break;
				case _INVDSTALPHA:
					s=GL_ONE_MINUS_DST_ALPHA;
					break;
				case _DSTCOLOR:
					s=GL_DST_COLOR;
					break;
				case _INVDSTCOLOR:
					s=GL_ONE_MINUS_DST_COLOR;
					break;

			};
		}

		if (RS->DstBlend!=_UNDEFINED)
		{
			switch (RS->DstBlend)
			{
				case _ZERO:
					d=GL_ZERO;
					break;
				case _ONE:
					d=GL_ONE;
					break;
				case _SRCALPHA:
					d=GL_SRC_ALPHA;
					break;
				case _INVSRCALPHA:
					d=GL_ONE_MINUS_SRC_ALPHA;
					break;
				case _SRCCOLOR:
					d=GL_SRC_COLOR;
					break;
				case _INVSRCCOLOR:
					d=GL_ONE_MINUS_SRC_COLOR;
					break;
				case _DSTALPHA:
					d=GL_DST_COLOR;
					break;
				case _INVDSTALPHA:
					d=GL_ONE_MINUS_DST_ALPHA;
					break;
				case _DSTCOLOR:
					d=GL_DST_COLOR;
					break;
				case _INVDSTCOLOR:
					d=GL_ONE_MINUS_DST_COLOR;
					break;

			};
		}

		if (RS->Blend!=_UNDEFINED)
		{
			switch(RS->Blend)
			{
			case _TRUE:
				glEnable(GL_BLEND);
				glBlendFunc(s,d);
				break;
			case _FALSE:
				glDisable(GL_BLEND);
				break;
			};
		}

		if (RS->Cull!=_UNDEFINED)
		{
			switch(RS->Cull)
			{
			case _NONE:
				render->SetParams(API3D_CULL,OFF);
				break;
			case _CCW:
				render->SetParams(API3D_CULL,CCW);
				break;
			case _CW:
				render->SetParams(API3D_CULL,CW);
				break;
			};
		}

		if (RS->RenderTarget!=_UNDEFINED)
		{
			switch (RS->RenderTarget)
			{
			case _RENDER:
				render->SetParams(API3D_RENDERTARGET,RENDER);
				break;
			case _STENCIL:
				render->SetParams(API3D_RENDERTARGET,STENCIL);
				break;
			case _ZBUFFER:
				render->SetParams(API3D_RENDERTARGET,ZBUFFER);
				break;
			case _BOTH:
				render->SetParams(API3D_RENDERTARGET,BOTH);
				break;
			case _BOTHZ:
				render->SetParams(API3D_RENDERTARGET,BOTH_WRITE_Z);
				break;
			};
		}

		if (RS->Stencil!=_UNDEFINED)
		{
			switch (RS->Stencil)
			{
			case _INCREMENT:
				render->SetParams(API3D_STENCIL,INCREMENT);
				break;
			case _DECREMENT:
				render->SetParams(API3D_STENCIL,DECREMENT);
				break;
			case _SET_ONE:
				render->SetParams(API3D_STENCIL,SET_ONE);
				break;
			case _EQUAL_ZERO:
				render->SetParams(API3D_STENCIL,EQUAL_ZERO);
				break;
			case _EQUAL_ONE:
				render->SetParams(API3D_STENCIL,EQUAL_ONE);
				break;
			case _ALWAYS:
				render->SetParams(API3D_STENCIL,ALWAYS);
				break;
			case _NOT_ONE:
				render->SetParams(API3D_STENCIL,NOT_ONE);
				break;
			case _NOT_ZERO:
				render->SetParams(API3D_STENCIL,NOT_ZERO);
				break;
			case _LESSEQUAL_ONE:
				render->SetParams(API3D_STENCIL,LESSEQUAL_ONE);
				break;
			case _GREATEREQUAL_ONE:
				render->SetParams(API3D_STENCIL,GREATEREQUAL_ONE);
				break;
			};
		}

	}
}

