		D3DDevice->SetSamplerState(1,D3DSAMP_ADDRESSU,D3DTADDRESS_WRAP);
		D3DDevice->SetSamplerState(1,D3DSAMP_ADDRESSV,D3DTADDRESS_WRAP);
		D3DDevice->SetSamplerState(1,D3DSAMP_MIPFILTER,D3DTEXF_NONE);

