				if (FAILED(D3DXCreateEffect( D3DDevice, SHADOW_VOLUME_SH_CarmackReverse2m, len_SHADOW_VOLUME_SH_CarmackReverse2m,NULL,NULL,0,NULL, &SHDVOLUME_CR2m, NULL ) ) ) return;

