				// REVERSE

				D3DXVECTOR4 Len(ActualVertexBuffer->fValue1,ActualVertexBuffer->fValue1,ActualVertexBuffer->fValue1,0);
				
				n=ActualVertexBuffer->iValue1;
				D3DXVECTOR4 Light(Lights[n].Pos.x,Lights[n].Pos.y,Lights[n].Pos.z,0.0f);

