					SHDVOLUME_CR1m->SetMatrix( "matViewProj", &matViewProj );
					SHDVOLUME_CR1m->SetVector( "Light", &Light);
					SHDVOLUME_CR1m->SetVector( "Len", &Len);
					SHDVOLUME_CR1m->SetVector( "Interpolant", &T);

