		Vertices[1].rhw=Vertices[0].rhw;
		Vertices[1].z=0.5f;

		Vertices[2].x=(x+sx);
		Vertices[2].y=(y+sy);
		Vertices[2].rhw=Vertices[0].rhw;
		Vertices[2].z=0.5f;

		Vertices[3].x=(x);
		Vertices[3].y=(y+sy);
		Vertices[3].rhw=Vertices[0].rhw;
		Vertices[3].z=0.5f;


		x1=0.0f+decalx;y1=0.0f+decaly;x2=1.0f;y2=1.0f;
