		x1=0.0f;y1=0.0f;x2=1.0f-decalx;y2=1.0f-decaly;
		Vertices[0].xm3=x1;Vertices[0].ym3=y1;
		Vertices[1].xm3=x2;Vertices[1].ym3=y1;
