/* ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////

  function:

	void C3DAPIBASE::SetFixedContextParameters(int stage,int (*fn)(int tag,int nt,int nt2,int nl,int nl2))

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////// */

void C3DAPIBASE::SetFixedContextParameters(int stage,int (*fn)(int nt,int nt2,int nl,int nlv,int tag))
{
	switch (stage)
	{
	case -1:
		fn_stage_draw=fn;
		break;
	case 0:
		fn_stage0=fn;
		break;
	case 1:
		fn_stage1=fn;
		break;
	case 2:
		fn_stage2=fn;
		break;
	case 3:
		fn_stage3=fn;
		break;
	case 4:
		fn_stage4=fn;
		break;
	case 5:
		fn_stage5=fn;
		break;
	};
}

