bool C3DAPIBASE::IsPrimarySurface()
{
#if defined(API3D_OPENGL)||defined(API3D_OPENGL20)||defined(API3D_METAL)
    if (SelectedRenderTarget == -1) return true;
#else
	if (SelectedRenderTarget == PRIMARY) return true;
#endif		    
	return false;
}

